import {expect} from "chai";
import {
    NetworkType,
    SignedTransaction,
    TransactionType,
} from 'symbol-sdk';
import {SignedTransactionQR} from "../src/SignedTransactionQR";
// internal dependencies

describe('SignedTransactionQR -->', () => {

    describe('toJSON() should', () => {

        it('include mandatory NIP-7 QR Code base fields', () => {
            // Arrange:
            const signedTransaction = new SignedTransaction(
                "",
                "443931795E15914146B774AE550762046525AF94E2C8E32F8DDFA9194D89A567",
                "443931795E15914146B774AE550762046525AF94E2C8E32F8DDFA9194D89A567",
                TransactionType.TRANSFER,
                NetworkType.TEST_NET,
            );


            // Act:
            const signedTx = new SignedTransactionQR(signedTransaction, NetworkType.TEST_NET, '');
            const actualJSON = signedTx.toJSON();
            const actualObject = JSON.parse(actualJSON);

            // Assert:
            expect(actualObject).to.have.property('v');
            expect(actualObject).to.have.property('type');
            expect(actualObject).to.have.property('network_id');
            expect(actualObject).to.have.property('chain_id');
            expect(actualObject).to.have.property('data');
        });

        it('include specialized schema fields', () => {
            // Arrange:
            const signedTransaction = new SignedTransaction(
                "",
                "443931795E15914146B774AE550762046525AF94E2C8E32F8DDFA9194D89A567",
                "443931795E15914146B774AE550762046525AF94E2C8E32F8DDFA9194D89A567",
                TransactionType.TRANSFER,
                NetworkType.TEST_NET,
            );

            // Act:
            const qr = new SignedTransactionQR(signedTransaction, NetworkType.TEST_NET, "443931795E15914146B774AE550762046525AF94E2C8E32F8DDFA9194D89A567");
            const actualJSON = qr.toJSON();
            const actualObject = JSON.parse(actualJSON);

            // Assert:
            expect(actualObject.data.payload).to.have.property('payload');
        });
    });
    describe('fromJSON() should', () => {
        it('reconstruct signed transaction', () => {
            // Arrange:
            const signedTransaction = new SignedTransaction(
                "",
                "443931795E15914146B774AE550762046525AF94E2C8E32F8DDFA9194D89A567",
                "443931795E15914146B774AE550762046525AF94E2C8E32F8DDFA9194D89A567",
                TransactionType.TRANSFER,
                NetworkType.TEST_NET,
            );
            const qr = new SignedTransactionQR(signedTransaction, NetworkType.TEST_NET, "443931795E15914146B774AE550762046525AF94E2C8E32F8DDFA9194D89A567");
            const mapper = (dto: any) => new SignedTransaction(dto.payload, dto.hash, dto.signerPublicKey, dto.type, dto.networkType);
            // Act:
            const reconstructedQR = SignedTransactionQR.fromJSON(qr.toJSON(), mapper);

            // Assert
            expect(qr.singedTransaction.toDTO().payload).to.be.equal(reconstructedQR.singedTransaction.toDTO().payload);
        });
    });
});
